// src/components/ProjectPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ProjectPage = () => {
  const { id } = useParams(); // Get the project ID from the URL

  // Here, we simulate fetching project details by ID (you can replace this with an actual API call)
  const [project, setProject] = useState(null);

  useEffect(() => {
    // Simulate fetching project details based on ID
    const projectData = {
      1: { name: 'Project 1', description: 'Detailed description of Project 1.' },
      2: { name: 'Project 2', description: 'Detailed description of Project 2.' },
      3: { name: 'Project 3', description: 'Detailed description of Project 3.' },
    };
    
    setProject(projectData[id]);
  }, [id]);

  if (!project) return <p>Loading project...</p>;

  return (
    <div>
      <h1>{project.name}</h1>
      <p>{project.description}</p>
      {/* You can add more details or options to track project progress here */}
    </div>
  );
};

export default ProjectPage;
