// src/components/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const projects = [
    { id: 1, name: 'Project 1', description: 'This is the first project.' },
    { id: 2, name: 'Project 2', description: 'This is the second project.' },
    { id: 3, name: 'Project 3', description: 'This is the third project.' },
  ];

  return (
    <div>
      <h1>Project List</h1>
      <div>
        {projects.map((project) => (
          <div key={project.id} className="project-item">
            <h2>{project.name}</h2>
            <p>{project.description}</p>
            <Link to={`/project/${project.id}`}>
              <button>View Project</button> {/* Button to navigate to the project details */}
            </Link>
            <Link to={`/track/${project.id}`}>
              <button>Track Project</button> {/* Button to track a project */}
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
